using System;\nusing System.Threading.Tasks;\nusing Microsoft.AspNetCore.Mvc;\nusing MyApp.Backend.Services;\nusing MyApp.Backend.DTOs;\n\nnamespace MyApp.Backend.Controllers\n{\n    [ApiController]\n    [Route(\"api/[controller]\")]\n    public class SupplierController : ControllerBase\n    {\n        private readonly ISupplierService _service;\n\n        public SupplierController(ISupplierService service)\n        {\n            _service = service;\n        }\n\n        [HttpPost]\n        public async Task<IActionResult> Create([FromBody] SupplierRequest request)\n        {\n            var result = await _service.CreateAsync(request);\n            return CreatedAtAction(nameof(GetById), new { id = result.Id }, result);\n        }\n\n        [HttpPut(\"{id:guid}\")]\n        public async Task<IActionResult> Update(Guid id, [FromBody] SupplierRequest request)\n        {\n            var result = await _service.UpdateAsync(id, request);\n            return Ok(result);\n        }\n\n        [HttpGet(\"{id:guid}\")]\n        public async Task<IActionResult> GetById(Guid id)\n        {\n            var result = await _service.GetByIdAsync(id);\n            return Ok(result);\n        }\n\n        [HttpGet]\n        public async Task<IActionResult> List()\n        {\n            var results = await _service.ListAsync();\n            return Ok(results);\n        }\n    }\n}