using System;\nusing MyApp.Backend.ValueObjects;\nusing MyApp.Backend.Models.Enums;\n\nnamespace MyApp.Backend.Models\n{\n    public class Supplier\n    {\n        public Guid Id { get; private set; }\n        public string Name { get; private set; }\n        public Cnpj Cnpj { get; private set; }\n        public PersonType PersonType { get; private set; }\n\n        protected Supplier() { }\n\n        public Supplier(string name, Cnpj cnpj, PersonType personType)\n        {\n            Id = Guid.NewGuid();\n            Name = name;\n            Cnpj = cnpj;\n            PersonType = personType;\n        }\n\n        public void Update(string name, Cnpj cnpj, PersonType personType)\n        {\n            Name = name;\n            Cnpj = cnpj;\n            PersonType = personType;\n        }\n    }\n}